<template>
  <div class="hdty-side hdty-side-theme" :visible="visible">
    <div class="hdty-card-header">
      <div class="hdty-card-title">
        主题风格
      </div>
    </div>
    <div class="hdty-card-body hdty-scroll">
      <div class="hdty-fluid">
        <div class="hdty-row hdty-col-space16">
          <div v-for="theme in themeData" class="hdty-col-md12">
            <div class="hdty-title hdty-title-plain">{{ theme.title }}</div>
            <div class="hdty-row hdty-col-space16">
              <div v-for="item in theme.text" class="hdty-col-md6">
                <div class="hdty-hover" :class="{ 'is-active':activeTheme===item.name }" @click.stop="toggleTheme(item.name)">
                  <img :src="item.image" class="hdty-btn-fluid">
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</template>

<script>
  export default {
    name: "SideTheme",
    data() {
      return {
        activeTheme: this.$store.state.app.theme || this.$app.themeDefault,
        // 主题数据
        themeData: [
          {
            title: '经典绿',
            text: [
              {
                name: 'classical-green',
                image: 'data:image/jpg;base64,'
              },
              {
                name: 'classical-green-header',
                image: 'data:image/jpg;base64,'
              }
            ]
          },
          {
            title: '警用蓝',
            text: [
              {
                name: 'police-blue',
                image: 'data:image/jpg;base64,'
              },
              {
                name: 'police-blue-header',
                image: 'data:image/jpg;base64,'
              }
            ]
          },
          {
            title: '深海蓝',
            text: [
              {
                name: 'ocean-blue-page',
                image: 'data:image/jpg;base64,'
              }
            ]
          }
        ],
        // 系统水印
        watermark: {
          isEnabled: this.$app.watermarkEnabled,
          text: this.$app.watermarkMaskEnabled ? this.$utilStr.replaceIdcardX(this.$utilLogin.getUserGmsfhm()) + ' ' + this.$utilStr.replaceNameX(this.$utilLogin.getUserXm()) || this.$utilLogin.getUsername() : this.$utilLogin.getUserGmsfhm() + ' ' + this.$utilLogin.getUserXm() || this.$utilLogin.getUsername()
        },
        // 显示side
        visible: false,
      }
    },
    watch: {
      visible: {
        handler(val) {
          if(val) {
            this.createMask();
          }else {
            this.removeMask();
          }
        },
        immediate: false
      }
    },
    methods: {
      // 切换主题
      toggleTheme(val){
        this.activeTheme = val;
        document.documentElement.setAttribute('theme-alias', val);
        document.body.setAttribute('layout-alias', val);
        this.$storage.local.set('hdty-admin-theme', val);
        this.$store.dispatch('editTheme', val);
        if(this.watermark.isEnabled) {
          this.$watermark.set(this.watermark.text);
        }
      },
      // 点击遮罩
      handleMask() {
        this.visible = false;
        this.$parent.opentheme = false;
      },
      // 移除遮罩
      removeMask() {
        let element = document.querySelector('#hd-mask');
        element.remove();
      },
      // 创建遮罩
      createMask() {
        let element = document.createElement('div');
        element.setAttribute('id', 'hd-mask');
        element.setAttribute('class', 'hdty-mask');
        element.onclick = this.handleMask;
        document.body.appendChild(element);
      }
    }
  }
</script>

